import React, { Component } from 'react';
import Buscador from './components/Buscador';
import Resultado from './components/Resultado';

class App extends Component{

    state = {
        termino:'',
        imagenes : [],
        pagina: ''
    }
    scroll = () => {
        const elemento = document.querySelector('.jumbotron');
        elemento.scrollIntoView('smooth', 'start');
    }

    paginaAnterior=()=>{
        console.log('anterior...');

          //console.log('siguiente...');
        //leer el state de la página actual
        let pagina = this.state.pagina;
        //condicion si pag=1, parar
        if(pagina === 1 ) return null;
        //resta uno a la página actual
        pagina -=1;

        // agregar el cambio al state
        this.setState({
            pagina
        }, () => {
            this.consultarApi();
            this.scroll();
        });
        //console.log(pagina);
    }
    paginaSiguiente = () => {
        //console.log('siguiente...');
        //leer el state de la página actual
        let pagina = this.state.pagina;
        //sumar uno a la página siguiente
        pagina = pagina + 1;

        // agregar el cambio al state
        this.setState({
            pagina
        }, () => {
            this.consultarApi();
            this.scroll();
        });
        //console.log(pagina);
    }

    consultarApi = () => {

        const termino = this.state.termino;
        //actualizar
        const pagina = this.state.pagina;

        const url =`https://pixabay.com/api/?key=24565416-af5f6e53807c085cc3685833b&q=${termino}&per_page=30&page=${pagina}`;

        console.log(url);

        fetch(url)
        .then(respuesta => respuesta.json())
        .then(resultado => this.setState({ imagenes : resultado.hits}))
    }

    datosBusqueda = (termino)=>{    
        
        this.setState({
            termino : termino,
            pagina: 1
        }, ()=> {
            this.consultarApi();
        })
    }
    
    render(){
        return(
            <div className="app container">
                <div className="jumbotron">
                    <p className="lead text-center text-black" >
                        Buscador de Imagénes</p>
                    <Buscador
                        datosBusqueda={this.datosBusqueda}
                    />

                </div>
                <div className="row justify-content-center">


                    <Resultado
                        imagenes={this.state.imagenes}
                        paginaAnterior={this.paginaAnterior}
                        paginaSiguiente={this.paginaSiguiente}
                    />
                </div>
            </div>
        );
    }
} 


export default App;
